public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// numbers
		// byte
		// short
		// int
		// long
		byte b = 12;
		short s = 1234;
		int i = 12345678; // focus
		long l = 1234567890123123432L;
		
		// decimals
		float f = 123.23f;
		double d = 1234.34; // focus
		
		boolean bool = true; // true or false
		bool = false;
		
		char ch = 'a'; //only use single quotes
		ch = 'A';
		
		// Non-primitive data types
		String name = "Java"; // Double quotes

	}

}
